import React from 'react';
import {BarController, Chart, defaults} from 'chart.js';
import { Line } from 'react-chartjs-2';

const state = {
  labels: ['2015', '2016', '2017', '2018',
           '2019'],
  datasets: [
    {
      label: 'Numbers of airstrikes',
      fill: false,
      lineTension: 0,
      backgroundColor: '#b32e51',
      borderColor: '#b32e51',
      borderWidth: 2,
      data: [ 40, 47, 33, 8, 3],
      data: [ 2020, 2019, 222, 2345, 113]
    },
  ]
}

export default class Patterns extends React.Component {
  render() {
    return (
      <div>
        <Line
          data={state}
          options={{
            title:{
              display:true,
              text:'Trends and patterns',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>
    );
  }
}
