import React from 'react';
import { Bar } from 'react-chartjs-2';

const state = {
  labels: ['Al-Hudaydah', 'Al-Mahwit', 'Amran', 'Dhamar','Hajja', 'Ibb', 'Lahj', 'Marib', 'Sa ada',
  'Sana a','Shabwa', 'Taiz' ],
  datasets: [
    {
      label: 'Numbers of airstrikes',
      fill: false,
      lineTension: 0,
      backgroundColor: '#b32e51',
      borderColor: '#b32e51',
      borderWidth: 2,
      data: [ 6, 17 , 16, 1, 15, 7, 4, 7, 21,
      20, 1, 16],
      options: {
        scales: {
            yAxes: [{
                stacked: true
            }]
        },
      },
    }
  ]
}

const options = {
    legend:{
      display: true,
      position:'right'
    },
    barPercentage: 0.9,
    categoryPercentage:0.10,

}

export default class Location extends React.Component {
  render() {
    return (
      <div>
        <Bar
          data={state}
          options={options}
        />
      </div>
    );
  }
}
