import React from 'react';
import { Bar } from 'react-chartjs-2';

const state = {
  labels: ['2015', '2016', '2017', '2018',
           '2019'],
  datasets: [
    {
      label: 'Numbers of airstrikes',
      fill: false,
      lineTension: 0,
      backgroundColor: '#b32e51',
      borderColor: '#b32e51',
      borderWidth: 2,
      data: [ 40, 47, 33, 8, 3],
      options: {
        scales: {
            yAxes: [{
                stacked: true
            }]
        },
      },
    }
  ]
}

const options = {
  scales: {
    yAxes: [
      {
        type: 'linear',
        display: true,
        position: 'left',
        id: 'y-axis-1',
      },
      {
        title:{
          text:'Trends and patterns',
          fontSize:20
        },
        legend:{
          display:false,
          position:'right'
        },
        type: 'linear',
        display: false,
        position: 'right',
        id: 'y-axis-2',
        gridLines: {
          drawOnArea: false,
        },
      },
    ],
  },
}
export default class Location extends React.Component {
  render() {
    return (
      <div>
        <Bar
          data={state}
          options={{
            title:{
              display:true,
              text:'Trends and patterns',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>
    );
  }
}
