import React from 'react';
import { Line } from 'react-chartjs-2';

const state = {
  labels: ['2015-Apr', '2015-May', '2015-Jul', '2015-Aug',
           '2015-Sep', '2015-Oct', '2015-Nov','2015-Dec','2016-Jan','2016-Feb',''],
  datasets: [
    {
      label: 'Numbers of airstrikes',
      fill: false,
      lineTension: 0,
      backgroundColor: '#b32e51',
      borderColor: '#b32e51',
      borderWidth: 2,
      data: [ 40, 47, 33, 8, 3],
      options: {
        scales: {
            yAxes: [{
                stacked: true

            }],
        },
      },
    }
  ]
}

export default class Patterns extends React.Component {
  render() {
    return (
      <div>
        <Line
          data={state}
          options={{
            title:{
              display:true,
              text:'Trends and patterns',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>
    );
  }
}
