import React from 'react';
import { Bar } from 'react-chartjs-2';

const state = {
  labels: ['2015', '2016', '2017', '2018',
           '2019'],
  datasets: [
    {
      label: 'Numbers of airstrikes',
      fill: false,
      lineTension: 0,
      backgroundColor: '#b32e51',
      borderColor: '#b32e51',
      borderWidth: 2,
      data: [ 40, 47, 33, 8, 3],
      options: {
        scales: {
            yAxes: [{
                stacked: true
            }]
        },
      },
    }
  ]
}

const options = {
  type: 'horizontalBar',
    title:{
      display:true,
      text:'Trends and patterns',
      fontSize:20
    },
    legend:{
      display:true,
      position:'right'
    },
    barPercentage: 0.9,
    categoryPercentage:0.10,

}

export default class Location extends React.Component {
  render() {
    return (
      <div>
        <Bar
          data={state}
          options={options}
        />
      </div>
    );
  }
}
